import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { logout, update } from '../actions/userActions';
import { useDispatch, useSelector } from 'react-redux';

function ProfileScreen(props) {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');
  const dispatch = useDispatch();

  const userSignin = useSelector(state => state.userSignin);
  const { userInfo } = userSignin;
  const handleLogout = () => {
    dispatch(logout());
    props.history.push("/signin");
  }
  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(update({ userId: userInfo._id, email, name, password }))
  }
  const userUpdate = useSelector(state => state.userUpdate);
  const { loading, success, error } = userUpdate;

  // const myOrderList = useSelector(state => state.myOrderList);
  // const { loading: loadingOrders, orders, error: errorOrders } = myOrderList;
  useEffect(() => {
    if (userInfo) {
      console.log(userInfo.name)
      setEmail(userInfo.email);
      setName(userInfo.name);
      setPassword(userInfo.password);
    }
    // dispatch(listMyOrders());
    return () => {

    };
  }, [userInfo])

  return <main className="ps-main">
  <div className="ps-contact ps-contact--2 ps-section pt-80 pb-80">
    <div className="ps-container">
      <div className="row">
        <div className="col-lg-6 col-md-6 col-sm-12 col-xs-12 ">
          <div className="ps-section__header pt-50">
            <h2 className="ps-section__title" data-mask="CHIKOIQUAN">
              - Create Account
      </h2>
            <form className="ps-contact__form" onSubmit={submitHandler} method="post">
              {loading && <div>Loading...</div>}
              {error && <div>{error}</div>}
              <div className="form-group">
                <label>
                  Name <sub>*</sub>
                </label>
                <input required name="name" id="name" className="form-control" onChange={(e) => setName(e.target.value)} type="name" placeholder />
              </div>
              <div className="form-group">
                <label>
                  Email <sub>*</sub>
                </label>
                <input required name="email" id="email" className="form-control" onChange={(e) => setEmail(e.target.value)} type="email" placeholder />
              </div>
              <div className="form-group">
                <button type="submit" className="ps-btn">
                  Register
            <i className="ps-icon-next" />
                </button>
              </div>
            </form>
          </div>
          <div className="ps-section__header pt-50">
            Already have an account? <Link to={redirect === "/" ? "signin" : "signin?redirect=" + redirect} className="button secondary text-center" >Login Your Account</Link>
          </div>
        </div>
      </div>
    </div>
  </div>
</main>
