<main className="ps-main">
  <div className="ps-contact ps-contact--2 ps-section pt-80 pb-80">
    <div className="ps-container">
      <div className="row">
        <div className="col-lg-6 col-md-6 col-sm-12 col-xs-12 ">
          <div className="ps-section__header pt-50">
            <h2 className="ps-section__title" data-mask="Contact">
              - Get in touch
            </h2>
            <form className="ps-contact__form" action="do_action" method="post">
              <div className="form-group">
                <label>
                  Name <sub>*</sub>
                </label>
                <input className="form-control" type="text" placeholder />
              </div>
              <div className="form-group">
                <label>
                  Email <sub>*</sub>
                </label>
                <input className="form-control" type="email" placeholder />
              </div>
              <div className="form-group mb-25">
                <label>
                  Your Message <sub>*</sub>
                </label>
                <textarea className="form-control" rows={6} defaultValue={""} />
              </div>
              <div className="form-group">
                <button className="ps-btn">
                  Send Message
                  <i className="ps-icon-next" />
                </button>
              </div>
            </form>
          </div>
        </div>
        <div className="col-lg-6 col-md-6 col-sm-12 col-xs-12 ">
          <div
            id="contact-map"
            data-address="New York, NY"
            data-title="Sky Store!"
            data-zoom={17}
          />
        </div>
      </div>
    </div>
  </div>
</main>

